<?phpclass AW_Rma_Block_Customer_Replaceinfo extends Mage_Core_Block_Template{	protected function _construct()    {        parent::_construct();        $this->setTemplate('aw_rma/customer/replace_order_info.phtml');    }				protected function _prepareLayout()    {        if ($headBlock = $this->getLayout()->getBlock('head')) {            $headBlock->setTitle($this->__('Order # %s', $this->getOrder()->getRealOrderId()));        }        $this->setChild(            'payment_info',            $this->helper('payment')->getInfoBlock($this->getOrder()->getPayment())        );    }			public function getPaymentInfoHtml()    {        return $this->getChildHtml('payment_info');    }    /**     * Retrieve current order model instance     *     * @return Mage_Sales_Model_Order     */    public function getOrder()    {        return Mage::registry('current_order');    }}