<?phpclass Mec_Rmaddon_Block_Adminhtml_Receivelist_Edit_Tab_Verify extends Mage_Adminhtml_Block_Abstract{	private $_receiveData = null;	private $_order = null;		public function __construct() {        if (!$this->getTemplate())            $this->setTemplate('mec/receivelist/verify.phtml');    }				public function getReceiveRequest() {        if (!$this->_receiveData)            $this->_receiveData = Mage::registry('receivelist_data');        return $this->_receiveData;    }		 public function getOrder() {        if (!$this->_order)            $this->_order = Mage::getModel('sales/order')->loadByIncrementId($this->getReceiveRequest()->getOrderId());        return $this->_order;    }	}